function lines(e){this.setDefaults();if(typeof e!="undefined"){if("canvas"in e)this.setCanvas(e.canvas);if("pieces"in e)this.setPieces(e.pieces);if("color"in e)this.setColor(e.color)}return this}lines.prototype.defaults={canvas:"lines-canvas",pieces:5,color:"#EEE"};lines.prototype.setCanvas=function(e){this.canvas=e;return this};lines.prototype.setPieces=function(e){this.pieces=e;return this};lines.prototype.setColor=function(e){this.color=e;return this};lines.prototype.clearCanvas=function(){var e=document.getElementById(this.canvas);var t=e.getContext("2d");t.clearRect(0,0,e.width,e.height);return this};lines.prototype.setDefaults=function(){this.canvas=this.defaults.canvas;this.pieces=this.defaults.pieces;this.color=this.defaults.color;return this};lines.prototype.draw=function(){this.clearCanvas();var e=document.getElementById(this.canvas);var t=e.getContext("2d");t.strokeStyle=this.color;t.beginPath();for(i=0;i<this.pieces;i++){var n=parseInt(Math.random()*2*e.height-e.height);var r=parseInt(Math.random()*2*e.height-e.height);if(n<0&&r<0)r+=parseInt(Math.random()*e.height);if(n>e.height&&r>e.height)r-=parseInt(Math.random()*e.height);t.moveTo(0,n);t.lineTo(e.width,r);t.stroke();console.log("Lines: Drawing piece "+(i+1)+" (0,"+n+") to ("+e.width+","+r+")")}return this}
